import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Preloader from '../Preloader/Preloader';
import './Movies.css';

function Movies() {
  const [isLoading, setIsLoading] = useState(false);
  // для проверки прелоадера
  // const [isLoading, setIsLoading] = useState(true);

  // для проверки верстки кнопок,
  // TODO: переделать на нормальную логику через стейты
  // function saveMovie() {
  //   document
  //     .querySelector('.movies-card__button_type_saved')
  //     .classList.toggle('movies-card__button_enabled');
  // }

  const [isSaved, setIsSaved] = useState(true);

  return (
    <section
      className='movies'
      aria-label='Фильмы'>
      <SearchForm />
      {isLoading ? (
        <Preloader />
      ) : (
        <>
          <MoviesCardList
            buttonsHtml={
              isSaved ? (
                <button
                  type='button'
                  className='movies-card__button movies-card__button_type_to-save'
                  onClick={!isSaved}
                  aria-label='Сохранить фильм'>
                  Сохранить
                </button>
              ) : (
                <button
                  type='button'
                  className='movies-card__button movies-card__button_style_round movies-card__button_type_saved'
                  aria-label='Фильм сохранен'></button>
              )
            }
          />
          <button
            className='movies__more-button'
            type='button'>
            Ещё
          </button>
        </>
      )}
    </section>
  );
}

export default Movies;
