import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Preloader from '../Preloader/Preloader';
import './SavedMovies.css';

function SavedMovies() {
  const [isLoading, setIsLoading] = useState(false);
  // для проверки прелоадера
  // const [isLoading, setIsLoading] = useState(true);

  document.querySelector('.movies-card__pic').addEventListener('click', () => {
    document
      .querySelector('.movies-card__remove-button')
      .classList.remove('movies-card__button_disabled');
  });

  return (
    <section
      className='saved-movies'
      aria-label='Сохраненные фильмы'>
      <SearchForm />
      {isLoading ? <Preloader /> : <MoviesCardList />}
    </section>
  );
}

export default SavedMovies;
