import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
// import Preloader from '../Preloader/Preloader';
import './Movies.css';
import Preloader from '../Preloader/Preloader';

function Movies() {
  // const [isLoading, setIsLoading] = useState(false);
  // для проверки прелоадера
  // const [isLoading, setIsLoading] = useState(true);

  const [isSearchSuccessful, setIsSearchSuccessful] = useState(true);

  //для проверки верстки кнопок
  const [isSaved, setIsSaved] = useState(true);
  function handleCardSaved() {
    setIsSaved(!isSaved);
  }

  return (
    <section
      className='movies'
      aria-label='Фильмы'>
      <SearchForm />
      {isSearchSuccessful ? (
        <>
          <MoviesCardList
            onPicClick={handleCardSaved}
            buttonsHtml={
              isSaved ? (
                <button
                  type='button'
                  className='movies-card__button movies-card__button_type_to-save'
                  aria-label='Сохранить фильм'>
                  Сохранить
                </button>
              ) : (
                <button
                  type='button'
                  className='movies-card__button movies-card__button_style_round movies-card__button_type_saved'
                  aria-label='Фильм сохранен'></button>
              )
            }
          />
          <button
            className='movies__more-button'
            type='button'>
            Ещё
          </button>
        </>
      ) : (
        <Preloader />
      )}

      {/* {isSearchSuccessful && (<>
        <MoviesCardList
          onPicClick={onPicClick}
          buttonsHtml={
            isSaved ? (
              <button
                type='button'
                className='movies-card__button movies-card__button_type_to-save'
                aria-label='Сохранить фильм'>
                Сохранить
              </button>
            ) : (
              <button
                type='button'
                className='movies-card__button movies-card__button_style_round movies-card__button_type_saved'
                aria-label='Фильм сохранен'></button>
            )
          }
        />
        <button
          className='movies__more-button'
          type='button'>
          Ещё
        </button>
      </>) : (<div></div>)} */}
    </section>
  );
}

export default Movies;
