import React from 'react';
import { Link } from 'react-router-dom';

import headerLogo from '../../images/logo.svg';
import './Logo.css';

function Logo({ currentPath }) {
  // const notInHeader = currentPath === '/signin' || currentPath === '/signup';

  return (
    <Link to='/'>
      <img
        // className={`logo ${notInHeader ? 'logo_no-margin' : ''}`}

        className='logo'
        src={headerLogo}
        alt='Логотип учебного проекта'
      />
    </Link>
  );
}

export default Logo;
