import React from 'react';
// import { Route, Routes } from 'react-router-dom';
import './MoviesCard.css';
import moviePic from '../../images/movie_card.jpg';

function MoviesCard({ buttonsHtml, isSaved }) {
  // для проверки верстки кнопок
  // function saveMovie() {
  //   document
  //     .querySelector('.movies-card__button_type_saved')
  //     .classList.toggle('movies-card__button_enabled');
  // }
  // const [isSaved, setIsSaved] = useState(true);

  return (
    <div className='movies-card'>
      <img
        className='movies-card__pic'
        alt='Обложка фильма'
        src={moviePic}
        onClick={!isSaved}
      />
      <div className='movies-card__info'>
        <p className='movies-card__name'>Здесь будет название фильма</p>
        <p className='movies-card__duration'>4ч 20м</p>
      </div>
      {buttonsHtml}
    </div>
  );
}

export default MoviesCard;
