import React, { useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Preloader from '../Preloader/Preloader';
import './Movies.css';

function Movies({ saved }) {
  const [isLoading, setIsLoading] = useState(false);
  // для проверки прелоадера
  // const [isLoading, setIsLoading] = useState(true);

  // document.querySelector('.movies-card__pic').addEventListener('click', () => {
  //   document
  //     .querySelector('.movies-card__saved-button')
  //     .classList.remove('movies-card__button_disabled');
  // });

  return (
    <section
      className='movies'
      aria-label='Фильмы'>
      <SearchForm />
      {isLoading ? (
        <Preloader />
      ) : (
        <>
          <MoviesCardList
            saved={saved}
            buttonsHtml={
              <>
                <button
                  type='button'
                  // className='movies-card__button movies-card__to-save-button movies-card__button_disabled'
                  className='movies-card__button movies-card__to-save-button'
                  // className='movies-card__button movies-card__to-save-button'
                  aria-label='Сохранить фильм'>
                  Сохранить
                </button>
                <button
                  type='button'
                  // className='movies-card__button movies-card__saved-button movies-card__button_disabled'
                  className={`movies-card__button movies-card__saved-button ${
                    saved ? '' : 'movies-card__button_disabled'
                  }`}
                  // {`movies-card__button movies-card__saved-button ${saved ? 'movies-card__button_disabled' : ''}`}
                  // className='movies-card__button movies-card__saved-button'
                  aria-label='Фильм сохранен'></button>
              </>
            }
          />
          <button
            className='movies__more-button'
            type='button'>
            Ещё
          </button>
        </>
      )}
    </section>
  );
}

export default Movies;
